  createSeries() {

    this.series = this.chart.series.push(new am4charts.ColumnSeries());
    this.series.dataFields.categoryY = "Category";
    this.series.dataFields.openValueX = this.selectedCountry1;
    this.series.dataFields.valueX = this.selectedCountry2;
    this.series.tooltipText = this.selectedCountry1 + " : {openValueX.value} " + this.selectedCountry2 + " : {valueX.value}";
    this.series.sequencedInterpolation = true;
    this.series.fillOpacity = 0;
    this.series.strokeOpacity = 1;
    this.series.columns.template.height = 0.01;
    this.series.tooltip.pointerOrientation = "vertical";

    let openBullet: any = this.series.bullets.create(am4charts.CircleBullet);
    openBullet.locationX = 1;
    openBullet.fill = this.chart.colors.getIndex(0);
    let closeBullet = this.series.bullets.create(am4charts.CircleBullet);

    closeBullet.fill = this.chart.colors.getIndex(6);
    closeBullet.stroke = closeBullet.fill;

    
    this.chart.legend.data = [{
      "name": this.selectedCountry1,
      "fill": this.chart.colors.getIndex(0)
    }, {
      "name": this.selectedCountry2,
      "fill": this.chart.colors.getIndex(6)
    }];
  }
  loadchart() {


    this.chart = am4core.create("chartdiv4", am4charts.XYChart);

    let data = this.data2018;

    this.chart.data = data;

    let categoryAxis = this.chart.yAxes.push(new am4charts.CategoryAxis());
    categoryAxis.renderer.grid.template.location = 0;
    categoryAxis.renderer.ticks.template.disabled = true;
    categoryAxis.renderer.axisFills.template.disabled = true;
    categoryAxis.dataFields.category = "Category";
    categoryAxis.renderer.minGridDistance = 15;
    categoryAxis.renderer.inversed = true;
    categoryAxis.renderer.inside = false;
    categoryAxis.renderer.grid.template.location = 0.5;
    categoryAxis.renderer.grid.template.strokeDasharray = "1,3";

    let valueAxis = this.chart.xAxes.push(new am4charts.ValueAxis());
    valueAxis.tooltip.disabled = true;
    valueAxis.renderer.ticks.template.disabled = true;
    valueAxis.renderer.axisFills.template.disabled = true;

    this.chart.legend = new am4charts.Legend();
    this.createSeries();

    

    this.chart.cursor = new am4charts.XYCursor();
    this.chart.cursor.behavior = "zoomY";

    // chart.scrollbarX = new am4core.Scrollbar();
    // chart.scrollbarY = new am4core.Scrollbar();
  }
